/*Table structure for table `emergency` */

DROP TABLE IF EXISTS `emergency`;

CREATE TABLE `emergency` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT '标题',
  `kind` int(10) unsigned NOT NULL COMMENT '分类',
  `photo` varchar(255) NOT NULL COMMENT '图片',
  `admin` varchar(50) NOT NULL COMMENT '添加人',
  `clicks` int(11) NOT NULL COMMENT '点击率',
  `content` longtext NOT NULL COMMENT '内容',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `emergency_emergencykind_id_fk` (`kind`),
  CONSTRAINT `emergency_emergencykind_id_fk` FOREIGN KEY (`kind`) REFERENCES `emergencykind` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

